 setSupportActionBar(toolbar);
 ActionBarDrawerToggle drawerToggle = new ActionBarDrawerToggle(
 this, drawerLayout, toolbar, R.string.open, R.string.close);
 drawerToggle.setDrawerIndicatorEnabled(true);
 drawerToggle.syncState();
 drawerLayout.addDrawerListener(drawerToggle);

 // Khởi tạo các fragments
 AdminRevenueFragment adminRevenueFragment = new AdminRevenueFragment();
 AdminOrderFragment adminOrderFragment = new AdminOrderFragment();
 AdminCategoryFragment adminCategoryFragment = new AdminCategoryFragment();
 AdminUserFragment adminUserFragment = new AdminUserFragment();
 AdminProductFragment adminAdditionalProductFragment = new AdminProductFragment();
