+ }
+
+ hideLoading();
+ }
+
+ private void placeOrder() {
+ // Kiểm tra dữ liệu nhập vào
+ String name = edtName.getText().toString().trim();
+ String phone = edtPhone.getText().toString().trim();
+ String address = edtAddress.getText().toString().trim();
+
